-- TABLE USERS
CREATE TABLE `dataku`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(100) NOT NULL , `phone` VARCHAR(20) NOT NULL , `password` VARCHAR(225) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;

-- TABLE MESSAGES
CREATE TABLE `dataku`.`messages` ( `id` INT NOT NULL AUTO_INCREMENT , `message` VARCHAR(225) NOT NULL , `sender_id` INT NOT NULL , `receiver_id` INT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;

-- TABLE MESSAGE_LOGS
CREATE TABLE `dataku`.`message_logs` ( `id` INT NOT NULL AUTO_INCREMENT , `message_id` INT NOT NULL , `receiver_id` INT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;

-- TABLE CONVERSATIONS
CREATE TABLE `dataku`.`conversations` ( `id` INT NOT NULL AUTO_INCREMENT , `message_id` INT NOT NULL , `participant` INT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;